import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CreateOptions.css';

function CreateOptions() {
  const navigate = useNavigate();
  const [selectedOption, setSelectedOption] = useState('');
  const [selectedType, setSelectedType] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedTemplate, setSelectedTemplate] = useState('');
  const [selectedResolution, setSelectedResolution] = useState('');

  const handleOptionClick = (option) => {
    setSelectedOption(option);
  };

  const handleTypeClick = (type) => {
    setSelectedType(type);
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  const handleTemplateClick = (template) => {
    setSelectedTemplate(template);
  };

  const handleResolutionClick = (resolution) => {
    setSelectedResolution(resolution);
  };

  const handleImageUpload = (event) => {
    // Assuming you will handle image upload logic here
  };

  const handleNextPage = () => {
    navigate('/video-preview');
  };

  const invitationTypes = ['Invitation Card', 'Promo Video'];
  const invitationCategories = ['Birthday Party', 'Wedding', 'House Warming', 'Funeral', 'Maturity Function', 'Dhothi Function'];
  const wishingCategories = ['Birthday Wishes', 'Wedding Anniversary Wishes', 'House Warming Greetings', 'Maturity Function', 'Dhothi Function'];
  const templates = ['Template 1.mp4', 'Template 2.mp4', 'Template 3.mp4']; // Example templates

  return (
    <div className="create-options">
      <div className="images-carousel">
        <img src={`${process.env.PUBLIC_URL}/images/image.gif`} alt=" abstract design" />
        <img src={`${process.env.PUBLIC_URL}/images/image1.gif`} alt="Colorful abstract design" />
        <img src={`${process.env.PUBLIC_URL}/images/image2.gif`} alt="Geometric shapes" />
        <img src={`${process.env.PUBLIC_URL}/images/image3.gif`} alt="Abstract patterns" />
        <img src={`${process.env.PUBLIC_URL}/images/image4.gif`} alt="Creative artwork" />
      </div>
      <div className="info-area">
        <h2>What would you like to create?</h2>
        <div className={`options ${selectedOption && 'fade-out'}`}>
          <button
            className={selectedOption === 'Invitation' ? 'selected' : ''}
            onClick={() => handleOptionClick('Invitation')}
          >
            Invitation
          </button>
          <button
            className={selectedOption === 'Wishing' ? 'selected' : ''}
            onClick={() => handleOptionClick('Wishing')}
          >
            Wishing
          </button>
        </div>
        {selectedOption && (
          <div className="types fade-in">
            <h3>Select Type:</h3>
            {selectedOption === 'Invitation' && invitationTypes.map((type) => (
              <button
                key={type}
                className={selectedType === type ? 'selected' : ''}
                onClick={() => handleTypeClick(type)}
              >
                {type}
              </button>
            ))}
          </div>
        )}
        {selectedType && (
          <div className="categories fade-in">
            <h3>Select Category:</h3>
            {(selectedOption === 'Invitation' ? invitationCategories : wishingCategories).map((category) => (
              <button
                key={category}
                className={selectedCategory === category ? 'selected' : ''}
                onClick={() => handleCategoryClick(category)}
              >
                {category}
              </button>
            ))}
          </div>
        )}
        {selectedCategory && (
          <div className="templates fade-in">
            <h3>Select Template:</h3>
            {templates.map((template) => (
              <button
                key={template}
                className={selectedTemplate === template ? 'selected' : ''}
                onClick={() => handleTemplateClick(template)}
              >
                {template}
              </button>
            ))}
          </div>
        )}
        {selectedTemplate && (
          <div className="resolutions fade-in">
            <h3>Choose Resolution:</h3>
            <button
              className={selectedResolution === '9:16' ? 'selected' : ''}
              onClick={() => handleResolutionClick('9:16')}
            >
              WhatsApp/Insta Stories (9:16)
            </button>
            <button
              className={selectedResolution === '16:9' ? 'selected' : ''}
              onClick={() => handleResolutionClick('16:9')}
            >
              Landscape Video (16:9)
            </button>
          </div>
        )}
        {selectedResolution && (
          <>
            <input type="file" accept="image/*" multiple onChange={handleImageUpload} />
            <button className="next-btn" onClick={handleNextPage}>Next</button>
          </>
        )}
      </div>
    </div>
  );
}

export default CreateOptions;
