import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './CreateOptions.css';

function CreateOptions() {
  const navigate = useNavigate();
  const [selectedOption, setSelectedOption] = useState('');
  const [selectedSubOption, setSelectedSubOption] = useState('');
  const [selectedVideo, setSelectedVideo] = useState('');
  const [transitioning, setTransitioning] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [userName, setUserName] = useState('');
  const [specialNote, setSpecialNote] = useState('');
  const [step, setStep] = useState(0); // New state to track the current step

  const images = [
    `${process.env.PUBLIC_URL}/images/one.gif`,
    `${process.env.PUBLIC_URL}/images/two.gif`,
    `${process.env.PUBLIC_URL}/images/three.gif`,
    `${process.env.PUBLIC_URL}/images/five.gif`,
    `${process.env.PUBLIC_URL}/images/six.gif`
  ];

  // Update video paths based on the Videos folder
  const videos = [
    `${process.env.PUBLIC_URL}/Videos/Video1.mp4`,
    `${process.env.PUBLIC_URL}/Videos/Video2.mp4`,
    `${process.env.PUBLIC_URL}/Videos/Video3.mp4`,
    `${process.env.PUBLIC_URL}/Videos/Video4.mp4`,
    `${process.env.PUBLIC_URL}/Videos/Video5.mp4`
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5900); // 5.9 seconds

    return () => clearInterval(interval);
  }, [images.length]);

  const handleOptionClick = (option) => {
    setTransitioning(true);
    setTimeout(() => {
      setSelectedOption(option);
      setTransitioning(false);
      setStep(1); // Move to the next step
    }, 500);
  };

  const handleSubOptionClick = (subOption) => {
    setTransitioning(true);
    setTimeout(() => {
      setSelectedSubOption(subOption);
      setTransitioning(false);
      setStep(2); // Move to the next step
    }, 500);
  };

  const handleVideoClick = (video) => {
    setTransitioning(true);
    setTimeout(() => {
      setSelectedVideo(video);
      setTransitioning(false);
      setStep(3); // Move to the next step
    }, 500);
  };

  const handleNextStep = () => {
    setStep((prevStep) => prevStep + 1);
  };

  const handleInputChange = (setter) => (event) => {
    setter(event.target.value);
  };

  const handleNextPage = () => {
    navigate('/video-preview');
  };

  return (
    <div className="create-options">
      <div className="images-carousel">
        {images.map((image, index) => (
          <img
            key={index}
            src={image}
            alt={`carousel-${index}`}
            className={index === currentImageIndex ? 'active' : ''}
          />
        ))}
      </div>
      <div className={`info-area ${transitioning ? 'fade-out' : 'fade-in'}`}>
        {step === 0 && (
          <div className="options">
            <h2>What would you like to create?</h2>
            <button onClick={() => handleOptionClick('Invitation')}>Invitation</button>
            <button onClick={() => handleOptionClick('Wishing')}>Wishing</button>
            <button onClick={() => handleOptionClick('Rang Rez Special Offerings')}>Rang Rez Special Offerings</button>
          </div>
        )}
        {step === 1 && selectedOption === 'Rang Rez Special Offerings' && (
          <div className="sub-options">
            <h3>Select Option:</h3>
            <button onClick={() => handleSubOptionClick('Buzzing Creations')}>Buzzing Creations</button>
            <button onClick={() => handleSubOptionClick('Rang Rez Standard')}>Rang Rez Standard</button>
          </div>
        )}
        {step === 2 && selectedSubOption && (
          <div className="videos">
            <h3>Select Video Template:</h3>
            <div className="video-gallery">
              {videos.map((video, index) => (
                <div key={index} className="video-option">
                  <video controls>
                    <source src={video} type="video/mp4" />
                    Your browser does not support the video tag.
                  </video>
                  <div className="video-controls">
                    <button className="play-btn">Play</button>
                    <button className="maximize-btn">Maximize</button>
                  </div>
                </div>
              ))}
            </div>
            <button className="next-btn" onClick={handleNextStep}>Next</button>
          </div>
        )}
        {step === 3 && (
          <div className="user-details">
            <h3>Enter Name:</h3>
            <input
              type="text"
              value={userName}
              onChange={handleInputChange(setUserName)}
              placeholder="Enter the name of the birthday person"
            />
            <button onClick={handleNextStep}>Next</button>
          </div>
        )}
        {step === 4 && (
          <div className="special-note">
            <h3>Enter Special Note:</h3>
            <input
              type="text"
              value={specialNote}
              onChange={handleInputChange(setSpecialNote)}
              placeholder="Enter a special note"
            />
            <button onClick={handleNextStep}>Next</button>
          </div>
        )}
        {step === 5 && (
          <div className="upload-images">
            <h3>Upload Images:</h3>
            <input type="file" accept="image/*" multiple />
            <button className="next-btn" onClick={handleNextPage}>Process Video</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default CreateOptions;
